import React, { useState } from 'react';

export default function ProductForm () {

const ProductForm = () => {
    const [id, setId] = useState('');
    const [nome, setNome] = useState('');
    const [imagem, setImagem] = useState(null);
    const [rating, setRating] = useState('');
    const [preco, setPreco] = useState('');
    const [link, setLink] = useState('');
    const [produtos, setProdutos] = useState([]);

    const handleIdChange = (event) => {
        setId(event.target.value);
    };

    const handleNomeChange = (event) => {
        setNome(event.target.value);
    };

    const handleImagemChange = (event) => {
        // Selecione a primeira imagem do arquivo de entrada
        setImagem(event.target.files[0]);
    };

    const handleRatingChange = (event) => {
        setRating(event.target.value);
    };

    const handlePrecoChange = (event) => {
        setPreco(event.target.value);
    };

    const handleLinkChange = (event) => {
        setLink(event.target.value);
    };

    const handleSubmit = (event) => {
        event.preventDefault();

        // Criar objeto do produto
        const novoProduto = {
            id: id,
            nome: nome,
            imagem: imagem,
            rating: rating,
            preco: preco,
            link: link
        };

        // Atualizar a lista de produtos
        setProdutos([...produtos, novoProduto]);

        // Limpar o formulário
        setId('');
        setNome('');
        setImagem(null);
        setRating('');
        setPreco('');
        setLink('');

        // Atualizar o arquivo JSON (opcional - você pode querer fazer isso em uma função separada)
        localStorage.setItem('produtos', JSON.stringify([...produtos, novoProduto]));

        alert('Produto cadastrado com sucesso!');
    };

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <label htmlFor="id">ID:</label>
                <input 
                    type="text" 
                    id="id" 
                    name="id" 
                    value={id} 
                    onChange={handleIdChange} 
                    required 
                /><br/><br/>

                <label htmlFor="nome">Nome do Produto:</label>
                <input 
                    type="text" 
                    id="nome" 
                    name="nome" 
                    value={nome} 
                    onChange={handleNomeChange} 
                    required 
                /><br/><br/>

                <label htmlFor="imagem">Imagem:</label>
                <input 
                    type="file" 
                    id="imagem" 
                    name="imagem" 
                    accept="image/*"
                    onChange={handleImagemChange} 
                    required 
                /><br/><br/>

                <label htmlFor="rating">Rating:</label>
                <input 
                    type="number" 
                    id="rating" 
                    name="rating" 
                    value={rating} 
                    onChange={handleRatingChange} 
                    required 
                /><br/><br/>

                <label htmlFor="preco">Preço:</label>
                <input 
                    type="number" 
                    id="preco" 
                    name="preco" 
                    value={preco} 
                    onChange={handlePrecoChange} 
                    required 
                /><br/><br/>

                <label htmlFor="link">Link:</label>
                <input 
                    type="text" 
                    id="link" 
                    name="link" 
                    value={link} 
                    onChange={handleLinkChange} 
                    required 
                /><br/><br/>

                <button type="submit">Cadastrar Produto</button>
            </form>

            <h2>Produtos Cadastrados:</h2>
            <ul>
                {produtos.map((produto, index) => (
                    <li key={index}>
                        <strong>{produto.nome}</strong> - Rating: {produto.rating} - Preço: {produto.preco} - Link: <a href={produto.link}>Ver Produto</a>
                    </li>
                ))}
            </ul>
        </div>
    );
};

}
